INSERT INTO `posts` (`id`, `title`, `subtitle`, `slug`, `body`, `status`, `posted_by`, `image`, `user_id`, `like`, `dislike`, `created_at`, `updated_at`) VALUES
(2, 'Thêm 6 nhân viên công ty Trường Sinh mắc COVID-19, cả nước đã có 194 ca', 'Bộ Y tế cho biết đã ghi nhận thêm 6 trường hợp mắc mới COVID-19 nâng tổng số ca mắc ở Việt Nam lên 194 ca.', 'them-6-nhan-vien-cong-ty-truong-sinh-mac-covid-19-ca-nuoc-da-co-194-ca', '<p><a href=\"https://kenh14cdn.com/2020/3/29/photo-1-1585460248929887360080.jpg\" target=\"_blank\" title=\"Ảnh minh hoạ\"><img alt=\"Thêm 6 nhân viên công ty Trường Sinh mắc COVID-19, cả nước đã có 194 ca - Ảnh 1.\" id=\"img_62f6d3f0-717f-11ea-b451-212dac43ced9\" src=\"https://kenh14cdn.com/thumb_w/620/2020/3/29/photo-1-1585460248929887360080.jpg\" title=\"Thêm 6 nhân viên công ty Trường Sinh mắc COVID-19, cả nước đã có 194 ca - Ảnh 1.\" /></a></p>\r\n\r\n<p>Ảnh minh hoạ</p>\r\n\r\n<p>S&aacute;ng nay 30/3, Bộ Y tế cho biết đ&atilde; ghi nhận th&ecirc;m 6 trường hợp mắc mới COVID-19 tại ổ dịch C&ocirc;ng ty TNHH Trường Sinh, đơn vị cung cấp dịch vụ cho Bệnh viện Bạch Mai, n&acirc;ng tổng số mắc tại Việt Nam l&ecirc;n 194 ca.</p>\r\n\r\n<p><strong>CA BỆNH 189 (BN189):</strong>&nbsp;Bệnh nh&acirc;n nữ, 46 tuổi, địa chỉ tại T&acirc;n Quang, N&ocirc;ng Cống, Thanh H&oacute;a, l&agrave; nh&acirc;n vi&ecirc;n thuộc bộ phận chuyển ph&aacute;t nước s&ocirc;i của C&ocirc;ng ty TNHH Trường Sinh tại c&aacute;c khoa/ph&ograve;ng Bệnh viện Bạch Mai.</p>\r\n\r\n<p>Bệnh nh&acirc;n ở c&ugrave;ng nh&agrave; với 2 trường hợp l&agrave;m c&ugrave;ng c&ocirc;ng ty dương t&iacute;nh với vi r&uacute;t SARS-CoV-2. Trong 2 tuần trở lại đ&acirc;y, bệnh nh&acirc;n kh&ocirc;ng đi đ&acirc;u xa khỏi khu&ocirc;n vi&ecirc;n bệnh viện. Ng&agrave;y 25/03/2020, bệnh nh&acirc;n xuất hiện triệu chứng ho, đau họng v&agrave; sốt.</p>\r\n\r\n<p>Ng&agrave;y 28/03/2020, bệnh nh&acirc;n được chuyển v&agrave;o c&aacute;ch ly tại Bệnh viện Bệnh Nhiệt đới Trung ương cơ sở Đ&ocirc;ng Anh, mẫu bệnh phẩm được chuyển đến Viện Vệ sinh Dịch tễ Trung ương. Hiện tại, bệnh nh&acirc;n đang được c&aacute;ch ly tại BVNĐTƯ cơ sở Đ&ocirc;ng Anh, t&igrave;nh trạng sức khoẻ ổn định.</p>\r\n\r\n<p><strong>CA BỆNH 190 (BN190):&nbsp;</strong>bệnh nh&acirc;n nữ, sinh năm 1971;</p>\r\n\r\n<p><strong>CA BỆNH 191 (BN191):&nbsp;</strong>bệnh nh&acirc;n nữ, sinh năm 1984;</p>\r\n\r\n<p><strong>CA BỆNH 192 (BN192):</strong>&nbsp;bệnh nh&acirc;n nữ, sinh năm 1997;</p>\r\n\r\n<p><strong>CA BỆNH 193 (BN193):&nbsp;</strong>bệnh nh&acirc;n nữ, sinh năm 1999;</p>', 1, NULL, 'public/lUjgMoaQ1q4zIRT2DOTj9svzy3YOHPpvobjrhw1m.jpeg', 2, NULL, NULL, '2020-03-25 21:30:07', '2020-03-29 20:54:47'),
(4, 'LUẬN Thế giới thay đổi từng giây, chỉ có sức mạnh \"đổi trắng thay đen\"', 'đọc những lời khen, được đếm số like tăng vùn vụt mà mừng thầm chứ sao nữa. Để hoàn thành mục tiêu nhỏ bé ấy, đừng trách', 'luan-the-gioi-thay-doi-tung-giay-chi-co-suc-manh-doi-trang-thay-den', '<p>Con g&aacute;i muốn g&igrave; khi đăng ảnh selflie l&ecirc;n mạng? Ngo&agrave;i việc để cập nhật về bản th&acirc;n th&igrave; l&yacute; do lớn nhất ắt hẳn l&agrave; để được đọc những lời khen, được đếm số like tăng v&ugrave;n vụt m&agrave; mừng thầm chứ sao nữa. Để ho&agrave;n th&agrave;nh mục ti&ecirc;u nhỏ b&eacute; ấy, đừng tr&aacute;ch con g&aacute;i khi lỡ d&ugrave;ng photoshop hơi nhiều, bởi lẽ đ&acirc;u phải c&ocirc; n&agrave;ng n&agrave;o sinh ra cũng xinh xắn đ&acirc;u.</p>\r\n\r\n<p>Suy cho c&ugrave;ng, tr&ecirc;n đời kh&ocirc;ng c&oacute; con g&aacute;i xấu, chỉ c&oacute; con g&aacute;i chưa biết l&agrave;m đẹp cho m&igrave;nh bằng photoshop th&ocirc;i m&agrave;!</p>\r\n\r\n<h3>Cần lắm một lời b&igrave;nh luận cho m&agrave;n biến h&oacute;a n&agrave;y</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2020/3/21/10-1-1584790302475177280667.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2020/3/21/10-1-1584790302475177280667.jpg\" title=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 1.\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2020/3/21/10-2-15847903024782013814385.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 1.\" src=\"https://kenh14cdn.com/thumb_w/660/2020/3/21/10-2-15847903024782013814385.jpg\" title=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 1.\" /></a></p>\r\n\r\n<hr />\r\n<h3>Kh&ocirc;ng phải ai trong ch&uacute;ng ta cũng ho&agrave;n hảo, những khuyết điểm ngoại h&igrave;nh khiến bạn tự ti nay c&oacute; thể bay biến bằng một v&agrave;i c&uacute; click chuột rồi</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2020/3/21/11-1-15847903176011828903235.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2020/3/21/11-1-15847903176011828903235.jpg\" title=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 3.\" /></a></p>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2020/3/21/11-2-1584790317604517027883.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 3.\" src=\"https://kenh14cdn.com/thumb_w/660/2020/3/21/11-2-1584790317604517027883.jpg\" title=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 3.\" /></a></p>\r\n\r\n<hr />\r\n<h3>Kh&ocirc;ng chỉ gi&uacute;p da mặt bạn mịn m&agrave;ng, trắng trẻo như quảng c&aacute;o m&agrave; photoshop c&ograve;n c&oacute; thể thay đổi cả g&oacute;c chụp gi&uacute;p bạn tr&ocirc;ng thon gọn hơn nữa</h3>\r\n\r\n<p><a href=\"https://kenh14cdn.com/2020/3/21/12-1-15847903306371537247829.jpg\" rel=\"lightbox\" title=\"\"><img alt=\"Thế giới thay đổi từng gi&amp;#226;y, chỉ c&amp;#243; sức mạnh &amp;quot;đổi trắng thay đen&amp;quot; của photoshop l&amp;#224; bất biến - Ảnh 5.\" src=\"https://kenh14cdn.com/thumb_w/660/2020/3/21/12-1-15847903306371537247829.jpg\" title=\"Thế giới thay đổi từng gi&amp